var CART_KEY = '@lojinhaShoppingCart';
export default {
  getCart: function getCart() {
    var productsString = localStorage.getItem(CART_KEY);

    if (!productsString) {
      return {
        products: [],
        total: 0
      };
    }

    var productsJson = this.jsonParseProducts(productsString);
    return productsJson;
  },
  addToCart: function addToCart(cart) {
    var productString = this.stringfyProducts(cart);
    localStorage.setItem(CART_KEY, productString);
  },
  stringfyProducts: function stringfyProducts(products) {
    return JSON.stringify(products);
  },
  jsonParseProducts: function jsonParseProducts(productsString) {
    return JSON.parse(productsString);
  }
};
